import React, { useEffect, useState } from 'react';
import ToastContainer from 'react-toastify';

import KeyJson from './keys.json';
import { updateMove } from './service';

import './App.css';

const App = () => {

	const [json, setJson] = useState(KeyJson);
	const [selected, setSelected] = useState({ key: '', value: '' });
	const [activePiece, setActivePiece] = useState('A');

	useEffect(() => {
		setJson(json);
	}, [json])

	const onSelect = (dict) => {

		const checkActivePiece = () => {
			return activePiece === dict.value.split('-')[0] || dict.value === '';
		}

		if (checkActivePiece()) {
			if (dict.value !== '') {
				setSelected(dict);
			} else {
				updateMove(dict, json, selected, activePiece)
					.then(res => {
						if (res.status === 200) {
							setJson(res.data.json);
							setActivePiece(res.data.activePiece)
						} else {
							console.log('Check--> ', res.data)
						}
					})
					.catch(e => { console.log(e); })
			}
		} else if (selected.value.split('-')[0] === activePiece && dict.value.split('-')[0] !== activePiece) {
			updateMove(dict, json, selected, activePiece)
				.then(res => {
					if (res.status === 200) {
						setJson(res.data.json);
						setActivePiece(res.data.activePiece)
					} else {
						console.log('Check--> ', res.data)
					}
				})
				.catch(e => { console.log(e); })
		} else {
			console.log('Check-->Can\'t make moves consecutively');
		}

	}

	return (
		<div className='container text-center align-items-center'>
			<ToastContainer />
			<div className='row m-1'>
				{
					json?.length > 0 && json.map(k => <div className='col-2' key={k.key} onClick={() => onSelect(k)}>{k.value}</div>)
				}
			</div>
		</div>
	);
}

export default App;
