import React, { useEffect, useState } from 'react';

import './App.css';
import KeyJson from './keys.json';
import { updateMove } from './service';

const App = () => {

	const [json, setJson] = useState(KeyJson);
	const [selected, setSelected] = useState({ key: "", value: "" });

	useEffect(() => {
		setJson(json);
	}, [json])

	const onSelect = (dict) => {

		const checkOppentPiece = (dict) => {
			if (dict.value !== "" && selected.value !== "") {
				let clickedPiece = dict.value.split('-')[0];
				let selectedPiece = selected.value.split('-')[0];
				console.log('Check-->condition: ', clickedPiece === selectedPiece);
			}
		}

		checkOppentPiece(dict);

		console.log('Check-->cond1: ', dict.value !== "")

		if (dict.value !== "") {
			setSelected(dict);
		} else {
			updateMove(dict, json, selected)
				.then(res => {
					if (res.status === 200) {
						setJson(res.data);
					} else {
						console.log('Check--> ', res.data)
					}
				})
				.catch(e => { console.log(e); })
		}

	}

	return (
		<div className="container text-center align-items-center">
			<div className="row m-1">
				{
					json?.length > 0 && json.map(k => <div className="col-2" key={k.key} onClick={() => onSelect(k)}>{k.value}</div>)
				}
			</div>
		</div>
	);
}

export default App;
