import React, { useEffect, useState } from 'react';

import './App.css';
import KeyJson from './keys.json';
import { updateMove } from './service';

const App = () => {

  const [json, setJson] = useState(KeyJson);
  const [selected, setSelected] = useState("");

  useEffect(() => {
    setJson(json);
  }, [json])

  const onSelect = (dict) => {

    if (dict.value !== "") {
      setSelected(dict);
    } else {
      json[Number(dict.key) - 1] = {
        key: dict.key,
        value: selected?.value
      };
      json[Number(selected.key) - 1] = {
        key: selected.key,
        value: ""
      };
    }



    updateMove(dict, json)
      .then(res => {
        setJson(res.data);
      })
      .catch(e => { })
  }

  return (
    <div className="container text-center align-items-center">
      <div className="row m-1">
        {
          json && json.length && json.map(k => <div className="col-2" key={k.key} onClick={() => onSelect(k)}>{k.value}</div>)
        }
      </div>
    </div>
  );
}

export default App;
